import React, { Component, useContext } from 'react';
import USAMap from "react-usa-map";

function stateChange() {
    setTimeout(function () {
    }, 5000);
}


class mapApp extends Component {
  /* mandatory */
  mapHandler = (event) => {
    alert(event.target.dataset.name + " needs 2500 more masks");
    this.statesCustomConfig('navy')
    this.componentDidMount(this)
  };


  statesCustomConfig = ( color ) => {
    return {
      "NJ": {
        fill: "navy",
        clickHandler: (event) => console.log('Custom handler for NJ', event.target.dataset)
      },
      "NY": {
        fill: "#CC0000"
      },
      "CA": {
        fill: "#CC0000"
      },
      "TN": {
        fill: "#CC0000"
      },
      "ND": {
        fill: "#CC0000"
      },
      "SD": {
        fill: "#CC0000"
      },
      "TX": {
        fill: "#CC0000"
      },
      "CO": {
        fill: "#CC0000"
      },
      "WA": {
        fill: "#CC0000"
      },
      "FL": {
        fill: color,
        clickHandler: (event) => {
            console.log('Custom handler for NJ', event.target.dataset)
            alert(event.target.dataset.name + " needs 2000 more masks");
            stateChange();
            this.statesCustomConfig( 'navy' )
        }
      }
    };
  };


  render() {
    return (
      <div className="App">
        <USAMap customize={this.statesCustomConfig( '#CC0000' )} onClick={this.mapHandler} width="650" height="650" />
        <button onClick={() => this.statesCustomConfig( '#CC0000' )}> Update </button>
      </div>
    );
  }
}

export default mapApp;
