        fill: "#CC0000"
      },
      "TX": {
        fill: "#CC0000"
      },
      "CO": {
        fill: "#CC0000"
      },
      "WA": {
        fill: "#CC0000"
      },
      "FL": {
        fill: color,
        clickHandler: (event) => {
            console.log('Custom handler for NJ', event.target.dataset)
            alert(event.target.dataset.name + " needs 2000 more masks");
            stateChange();
            this.statesCustomConfig( 'navy' )
        }
      }
    };
  };


    return (
      <div className="App">
        <USAMap customize={this.statesCustomConfig( '#CC0000' )} onClick={this.mapHandler} width="650" height="650" />
      </div>
    );

}

export default mapApp;
